<?php	function storeEmail () {				if (!$_POST['email']) {			$response = array('success' => 0, 'message' => 'Please enter an email address.');			return json_encode($response);		} else if (!preg_match("/^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*$/i", $_POST['email'])) {			$response = array('success' => 0, 'message' => 'Email address is invalid.');			return json_encode($response);		}		require_once('MCAPI.class.php');		$api = new MCAPI('251a1c4bfffd818155141030c300fada-us6');				$listId = 'ff1ba26281';		if ($api->listSubscribe($listId, $_POST['email']) === true) {			$response = array('success' => 1, 'message' => 'Success! Check your email to confirm sign up.');			return json_encode($response);		} else {			$response = array('success' => 0, 'message' => $api->errorMessage);			return json_encode($response);		}			}	echo storeEmail();